export const authReasons = {
  UNCONFIRMED: {
    message: "You probably need to receive a new confirmation",
    link: "http://localhost:3000/auth/resend",
    color: "red",
  },

  INVALID: {
    message: "The email or password you've provided are invalid",
    color: "red",
  },
};
